INSERT INTO `movie` VALUES (35240292, '分睡契约', '李华彤', '91分钟', '2020-11-11(中国大陆)', '', '普通家庭的青年毕青松，为了梦想在北京奋斗。他在送快递的过程中发现了新的商机，研发了一款夜间配送的APP。楚月与大自己二十岁的房地产商费总分手后，通过合租信息与毕青松相识相恋，后来由于种种矛盾离开，各自的事业稳步上升，但心里还挂念彼此。', 'https://img2.doubanio.com/view/photo/s_ratio_poster/public/p2624390583.jpg', NULL, NULL, '10,9', '[{\"name\":\"李华彤\",\"avatar\":\"https://img9.doubanio.com/view/celebrity/s_ratio_celebrity/public/p1363320580.25.jpg\"},{\"name\":\"田媛\",\"avatar\":\"https://img3.doubanio.com/f/movie/8dd0c794499fe925ae2ae89ee30cd225750457b4/pics/movie/celebrity-default-medium.png\"},{\"name\":\"欧阳培龙\",\"avatar\":\"https://img1.doubanio.com/view/celebrity/s_ratio_celebrity/public/p1491820295.87.jpg\"},{\"name\":\"白马竞\",\"avatar\":\"https://img1.doubanio.com/view/celebrity/s_ratio_celebrity/public/p1493447278.39.jpg\"}]', '1', NULL);
