INSERT INTO `movie` VALUES (35225979, '保家卫国——抗美援朝光影纪实', '郝蕴', '82分钟', '2020-10-25(中国大陆)', '', '电影展现了抗美援朝战争历时2年零9个月后胜利结束的峥嵘历程。战争激发了全国人民热火朝天的生产建设积极性，前方和后方相互激励、共同推进。“中国人民志愿军”成为坚强、不屈、勇敢的代名词，永远铭刻于世界战史。', 'https://img1.doubanio.com/view/photo/s_ratio_poster/public/p2623585039.jpg', 'https://img9.doubanio.com/img/trailer/medium/2624318456.jpg?', 'https://vt1.doubanio.com/202011130800/0d7ea3a19f2a166343e1ed81c29a117e/view/movie/M/402670945.mp4', '22', '[{\"name\":\"郝蕴\",\"avatar\":\"https://img9.doubanio.com/view/celebrity/s_ratio_celebrity/public/p1603160458.05.jpg\"},{\"name\":\"张涵予\",\"avatar\":\"https://img1.doubanio.com/view/celebrity/s_ratio_celebrity/public/p49047.jpg\"},{\"name\":\"焦宏奋\",\"avatar\":\"https://img2.doubanio.com/view/celebrity/s_ratio_celebrity/public/pFh0GJQN2lNkcel_avatar_uploaded1551726337.83.jpg\"},{\"name\":\"傅若清\",\"avatar\":\"https://img9.doubanio.com/view/celebrity/s_ratio_celebrity/public/p1564995850.05.jpg\"},{\"name\":\"于冬\",\"avatar\":\"https://img2.doubanio.com/view/celebrity/s_ratio_celebrity/public/p41523.jpg\"},{\"name\":\"姜海清\",\"avatar\":\"https://img9.doubanio.com/view/celebrity/s_ratio_celebrity/public/p1555835394.56.jpg\"}]', '1', NULL);
